/* Author: Bethlehem Shimelis
   Event: Unified Login + OTP + Registration Flow
   LatestUpdate: Floating food emoji background with OTP verification before registration
*/
import { useState, useEffect, useMemo } from "react";
import axios from "axios";
import "../auth.css";

const API_BACKEND = process.env.REACT_APP_API_BACKEND;

const foodEmojis = [
  'ðŸŽ','ðŸŒ','ðŸŠ','ðŸ','ðŸ‰','ðŸ‡','ðŸ“',
  'ðŸ¥•','ðŸŒ½','ðŸ¥”','ðŸ¥¦','ðŸ¥¬','ðŸ§„','ðŸ§…',
  'ðŸž','ðŸ¥–','ðŸ¥¯','ðŸ¥',
  'ðŸ¥š','ðŸ§€','ðŸ¥›',
  'ðŸ¥«','ðŸ«™','ðŸ²','ðŸ¥£',
  'ðŸš','ðŸ','ðŸŒ¾',
  'ðŸ¥œ','ðŸŒ°',
  'ðŸ¯','ðŸ¼','ðŸ§º',
  'ðŸª','ðŸ«'
];

const regions = [ 
  "Durban, KwaZulu-Natal","Cape Town, Western Cape","Pretoria, Gauteng",
  "Johannesburg, Gauteng","Port Elizabeth (Gqeberha), Eastern Cape","Bloemfontein, Free State",
  "Nelspruit (Mbombela), Mpumalanga","Kimberley, Northern Cape","Potchefstroom, North West",
  "Polokwane, Limpopo","George, Western Cape","East London, Eastern Cape",
  "Welkom, Free State","Mthatha, Eastern Cape","Stellenbosch, Western Cape",
  "Pietermaritzburg, KwaZulu-Natal"
];

export default function UnifiedAuth({ onLogin }) {
  const [step, setStep] = useState("login"); // login | otp | register
  const [form, setForm] = useState({
    email: "", password: "", name: "", accountType: "Household/Individual",
    region: "", capacity: ""
  });
  const [otp, setOtp] = useState("");
  const [emojiIndex, setEmojiIndex] = useState(0);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [resendCooldown, setResendCooldown] = useState(0);
  const [regionSearch, setRegionSearch] = useState("");
  const [showDropdown, setShowDropdown] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => setEmojiIndex(prev => (prev + 1) % foodEmojis.length), 1000);
    return () => clearInterval(interval);
  }, []);

  // OTP resend cooldown
  useEffect(() => {
    if (resendCooldown <= 0) return;
    const timer = setInterval(() => setResendCooldown(prev => prev - 1), 1000);
    return () => clearInterval(timer);
  }, [resendCooldown]);

  const filteredRegions = useMemo(() => {
    return regions.filter(r => r.toLowerCase().includes(regionSearch.toLowerCase()));
  }, [regionSearch]);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleRegionSelect = r => {
    setForm(prev => ({ ...prev, region: r }));
    setRegionSearch(r);
    setShowDropdown(false);
  };

  const requestOtp = async () => {
    try {
      await axios.post(`${API_BACKEND}/api/auth/request-otp`, { email: form.email });
      setSuccess("OTP sent! Check your email.");
      setStep("otp");
      setResendCooldown(30);
    } catch (err) {
      setError(err.response?.data?.error || "Failed to request OTP");
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError(""); setSuccess("");

    if (step === "login") {
      try {
        const res = await axios.post(`${API_BACKEND}/api/auth/login`, { email: form.email, password: form.password });
        setSuccess("Login successful!");
        if (onLogin) onLogin(res.data.user);
        localStorage.setItem("loggedInUser", JSON.stringify(res.data.user));
      } catch (err) {
        const msg = err.response?.data?.error || "Login failed";
        if (msg.includes("Invalid credentials") || msg.includes("Email not registered")) {
          requestOtp();
        } else setError(msg);
      }
    } else if (step === "otp") {
      try {
        await axios.post(`${API_BACKEND}/api/auth/verify-otp`, { email: form.email, otp });
        setSuccess("OTP verified! Complete registration.");
        setStep("register");
      } catch (err) {
        setError(err.response?.data?.error || "OTP verification failed");
      }
    } else if (step === "register") {
      if (form.accountType === "Charity/Foodbank" && (!form.capacity || isNaN(form.capacity))) {
        setError("Please enter a valid capacity");
        return;
      }
      try {
        const payload = { ...form };
        const res = await axios.post(`${API_BACKEND}/api/auth/register`, payload);
        setSuccess("Registration complete!");
        if (onLogin) onLogin(res.data.user);
        localStorage.setItem("loggedInUser", JSON.stringify(res.data.user));
      } catch (err) {
        setError(err.response?.data?.error || "Registration failed");
      }
    }
  };

  const handleResend = () => { if (resendCooldown <= 0) requestOtp(); };

  const emojiSpans = useMemo(() => {
    return Array.from({ length: 100 }, (_, i) => {
      const emoji = foodEmojis[i % foodEmojis.length];
      const left = Math.random() * 100;
      const duration = 10 + Math.random() * 6;
      const delay = Math.random() * 10;
      return (
        <span
          key={i}
          className="food-item"
          style={{ left: `${left}%`, fontSize: '60px', animationDuration: `${duration}s`, animationDelay: `${delay}s`, opacity:0, animationFillMode:'forwards' }}
        >{emoji}</span>
      );
    });
  }, []);

  return (
    <div className="login-page">
      <div className="food-background">{emojiSpans}</div>
      <div className="login-card-wrapper">
        <form onSubmit={handleSubmit} className="form-card">
          <div className="form-header">
            <div className="logo-icon" style={{ fontSize: '3rem' }}>{foodEmojis[emojiIndex]}</div>
            <h1 className="app-title">
              {step === "login" ? "FoodSave Hub" : step === "otp" ? "Enter OTP" : "Complete Registration"}
            </h1>
            <p className="subtitle">
              {step === "login" ? <em>Recreate, Donate, and Reduce Food Waste</em>
               : step === "otp" ? "Check your email for the OTP"
               : <em>Fill in your details to register</em>}
            </p>
          </div>

          {/* Email */}
          <input type="email" name="email" placeholder="Enter your email" value={form.email} onChange={handleChange} required className="input-field" disabled={step !== "login"} />

          {/* Password (login only) */}
          {step === "login" && <input type="password" name="password" placeholder="Enter your password" value={form.password} onChange={handleChange} required className="input-field" />}

          {/* OTP input */}
          {step === "otp" && (
            <>
              <input type="text" name="otp" placeholder="Enter OTP" value={otp} onChange={e => setOtp(e.target.value)} required className="input-field" />
              <button type="button" className="toggle-link" onClick={handleResend} disabled={resendCooldown > 0}>
                {resendCooldown > 0 ? `Resend OTP in ${resendCooldown}s` : "Resend OTP"}
              </button>
            </>
          )}

          {/* Registration form */}
          {step === "register" && (
            <>
              <select name="accountType" value={form.accountType} onChange={handleChange} className="input-field">
                <option>Household/Individual</option>
                <option>Business/Corporate</option>
                <option>Charity/Foodbank</option>
              </select>
              <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} className="input-field" required />
              <input type="password" name="password" placeholder="Password" value={form.password} onChange={handleChange} className="input-field" required />
              <div className="region-search-container">
                <input type="text" placeholder="Search Region..." value={regionSearch} onChange={e => { setRegionSearch(e.target.value); setShowDropdown(true); }} onFocus={() => setShowDropdown(true)} onBlur={() => setTimeout(() => setShowDropdown(false), 1000)} className="region-search-input" />
                {showDropdown && filteredRegions.length > 0 && (
                  <div className="region-dropdown">{filteredRegions.map((r,i) => <div key={i} onClick={() => handleRegionSelect(r)} className="region-option">{r}</div>)}</div>
                )}
              </div>
              {form.accountType === "Charity/Foodbank" && (
                <input type="number" name="capacity" placeholder="Capacity" value={form.capacity} onChange={handleChange} min="0" className="input-field" required />
              )}
            </>
          )}

          {error && <p className="error-msg">{error}</p>}
          {success && <p className="success-msg">{success}</p>}

          <button type="submit" className="primary-btn">
            {step === "login" ? "Login" : step === "otp" ? "Verify OTP" : "Register"}
          </button>

          {step === "login" && <button type="button" className="toggle-link" onClick={requestOtp}>Don't have an account? Sign Up</button>}
        </form>
      </div>
    </div>
  );
}
