import React, { Component } from 'react';
import axios from "axios";
import "./Recipe.css";

const API_BACKEND = process.env.REACT_APP_API_BACKEND;

class Recipe extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedItems: [],
      items: [],
      filter: "",          // dropdown filter
      recipes: [],         // store generated recipes
      showGeneratedPanel: false,  // toggle separate panel
      loading: true,
      error: "",
      aiMessage: "This is a temporary OpenAI message."  // store AI response message
    };
  }

  componentDidMount() {
    this.fetchExpiringItems();
  }
  
  fetchExpiringItems = async () => {
    try {
      const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
      if (!loggedInUser) {
        this.setState({ error: "No logged-in user found.", loading: false });
        return;
      }

      const res = await axios.get(`${API_BACKEND}/api/notifications`, {
        params: { 
          email: loggedInUser.email,
          days: 7
         }
      });

      // âœ… API already filters items expiring in <= 2 days
      const items = res.data.map((item) => item.name);

      this.setState({
        items: items.length > 0 ? items : [
          "No items expiring soon"
        ],
        loading: false,
        error: ""
      });
    } catch (err) {
      console.error("Error fetching expiring items:", err);
      this.setState({
        error: "Failed to fetch expiring items.",
        loading: false
      });
    }
  };

  toggleItem = (item) => {
    this.setState((prevState) => {
      const alreadySelected = prevState.selectedItems.includes(item);
      return {
        selectedItems: alreadySelected
          ? prevState.selectedItems.filter((i) => i !== item)
          : [...prevState.selectedItems, item]
      };
    });
  };

  handleFilterChange = (e) => {
    this.setState({ filter: e.target.value });
  };

  /*handleGenerate = () => {
    // Placeholder recipes for demo
    const placeholderRecipes = [
      { 
        title: "Recipe 1", 
        prepTime: "15 mins", 
        image: "https://images.unsplash.com/photo-1466637574441-749b8f19452f?q=80&w=880&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D", 
        ingredients: ["1 cup rice", "2 cups water", "Salt"],
        steps: ["Boil water", "Add rice", "Cook 15 mins"],
        expanded: false  // Explicitly set expanded property
      },
      { 
        title: "Recipe 2", 
        prepTime: "20 mins", 
        image: "https://images.unsplash.com/photo-1506368249639-73a05d6f6488?q=80&w=687&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D", 
        ingredients: ["2 eggs", "1 tbsp milk", "Salt & pepper"],
        steps: ["Whisk eggs", "Heat pan", "Cook eggs 5 mins"],
        expanded: false  // Explicitly set expanded property
      },
      { 
        title: "Recipe 3", 
        prepTime: "30 mins", 
        image: "https://images.unsplash.com/photo-1679556217543-c0cc895892f5?q=80&w=1170&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D", 
        ingredients: ["1 chicken breast", "Spices", "Olive oil"],
        steps: ["Season chicken", "Bake 25 mins", "Serve hot"],
        expanded: false  // Explicitly set expanded property
      }
    ];

    this.setState({ 
      recipes: placeholderRecipes, 
      showGeneratedPanel: true 
    });
  };*/
  handleGenerate = async () => {
  const { selectedItems, filter } = this.state;

  if (selectedItems.length === 0) {
    alert("Please select at least one ingredient to generate a recipe.");
    return;
  }

  try {
    // Call backend API
    const res = await axios.post(`${API_BACKEND}/api/recipe/generate`, {
  ingredients: selectedItems,
  preferences: filter
});

    const hardcodedImages = [
  "https://images.unsplash.com/photo-1466637574441-749b8f19452f?q=80&w=880&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
  "https://images.unsplash.com/photo-1506368249639-73a05d6f6488?q=80&w=687&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
  "https://images.unsplash.com/photo-1679556217543-c0cc895892f5?q=80&w=1170&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
];

    const recipes = res.data.suggestions.map((recipe, index) => ({
      ...recipe,
      expanded: false, // ensure expanded property exists for each recipe
      image: hardcodedImages[index] || "https://images.unsplash.com/photo-1692288843207-786c8cb62e7a?q=80&w=1370&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
    }));

    this.setState({
      recipes,
      showGeneratedPanel: true
    });
  } catch (err) {
    console.error("Error generating recipes:", err);
    alert("Failed to generate recipes. Please try again.");
  }
};


  // Toggle function - FIXED
  toggleExpand = (index) => {
    console.log('Toggle clicked for index:', index); // Debug log
    this.setState(prevState => {
      const updatedRecipes = prevState.recipes.map((recipe, i) => {
        if (i === index) {
          return { ...recipe, expanded: !recipe.expanded };
        }
        return recipe;
      });
      console.log('Updated recipes:', updatedRecipes); // Debug log
      return { recipes: updatedRecipes };
    });
  };

  handleBack = () => {
    this.setState({ showGeneratedPanel: false, recipes: [] });
  };

  handleSave = (recipe) => {
    alert(`Saved ${recipe.title}!`);
  };
 
  render() {
    const { selectedItems, items, filter, recipes, showGeneratedPanel, loading, error } = this.state;

    if (loading) return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading your food items....</p>
      </div>
    );

    if (error) {
      return <p className="error-msg">{error}</p>;
    }

    // Original generate panel
    if (!showGeneratedPanel) {
      return (
        <div className="notification-wrapper" style={{ display: "flex", gap: "20px" }}>

          <div className="recipe-container">
            <div className="recipe-card">
              <h2 className="recipe-title">AI Recipe Generator</h2>
              <p className="recipe-subtitle">Your food items that are soon to expire are listed below:</p>

              <div className="recipe-list">
                {items.map((item, index) => (
                  <label key={index}>
                    <input
                      type="checkbox"
                      checked={selectedItems.includes(item)}
                      onChange={() => this.toggleItem(item)}
                    />
                    {item}
                  </label>
                ))}
              </div>

              <div className="filter-dropdown">
                <label htmlFor="recipe-filter">Filter Recipes: </label>
                <select id="recipe-filter" value={filter} onChange={this.handleFilterChange}>
                  <option value="">All</option>
                  <option value="Vegan">Vegan</option>
                  <option value="Vegetarian">Vegetarian</option>
                  <option value="Gluten-Free">Gluten-Free</option>
                </select>
              </div>

              <button className="generate-btn" onClick={this.handleGenerate}>
                Generate
              </button>
              <button className="generate-btn" onClick={this.fetchSavedRecipes}>
  View Saved Recipes
</button>
            </div>
          </div>
        </div>
      );
    }

    // Generated recipes panel
    return (
      <div className="recipe-container">
        <div className="recipe-card">
          <h2 className="recipe-title">Generated Recipes</h2>
          <p style={{ fontStyle: "italic", color: "#555", marginBottom: "10px" }}>
  Click anywhere on a recipe card to view the details
</p>
          <div className="recipe-results">
            {recipes.map((recipe, index) => (
              <div 
                key={index} 
                className="recipe-result-card"
                onClick={() => this.toggleExpand(index)}
              >
                {!recipe.expanded ? (
                  // Summary view (original content)
                  <>
                    <img src={recipe.image} alt={recipe.title} />
                    <h3>{recipe.title}</h3>
                    <p>Prep: {recipe.prepTime}</p>
                    <button 
                      className="save-btn" 
                      onClick={(e) => {
                        e.stopPropagation(); // Prevent card toggle when clicking save
                        this.handleSave(recipe);
                      }}
                    >
                      Save
                    </button>
                  </>
                ) : (
                  // Detailed view (expanded content)
                  <>
                  {/* Image at top of details */}
    <img 
      src={recipe.image} 
      alt={recipe.title} 
      className="recipe-detail-image"
    />
                    <h3>{recipe.title}</h3>
                    <p>Prep Time: {recipe.prepTime}</p>
                    
                    <div className="recipe-details">
                      <h4>Ingredients:</h4>
                      <ul>
                        {recipe.ingredients.map((ingredient, i) => (
                          <li key={i}>{ingredient}</li>
                        ))}
                      </ul>
                      
                      <h4>Steps:</h4>
                      <ol>
                        {recipe.steps.map((step, i) => (
                          <li key={i}>{step}</li>
                        ))}
                      </ol>
                    </div>
                    
                    <button 
                      className="save-btn" 
                      onClick={(e) => {
                        e.stopPropagation(); // Prevent card toggle when clicking save
                        this.handleSave(recipe);
                      }}
                    >
                      Save
                    </button>
                  </>
                )}
              </div>
            ))}
          </div>

          <button className="generate-btn" style={{ marginTop: "20px" }} onClick={this.handleBack}>
            Back
          </button>
        </div>
      </div>
    );
  }
}

export default Recipe;
