import React, { useEffect, useState, useContext, useRef } from "react"; 
import { useParams, useNavigate, useLocation } from "react-router-dom";
import ChatContext from "./ChatContext";
import "./ChatThread.css";

/* Author: Lethabo Mazui
   Event: Sprint 1
   LatestUpdate: Added ChatThread component
   Description: Handles display and interaction for a single chat conversation
*/
export default function ChatThread() {
  const { donationId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { channels, markChatRead, markDelivered, currentUserId, addMessage } = useContext(ChatContext);

  const [typing, setTyping] = useState(false);
  const [inputValue, setInputValue] = useState("");
  const [avatarEmoji, setAvatarEmoji] = useState(location.state?.avatarEmoji || "üçè");
  const [otherParticipant, setOtherParticipant] = useState(location.state?.participantName || "User");
  const messagesEndRef = useRef(null);

  const currentYear = new Date().getFullYear();

  /* Author: Lethabo Mazui
     Event: Sprint 1
     LatestUpdate: Added helper for ISO conversion
     Description: Converts timestamp to local ISO string (+2h) safely
  */
  const toLocalISO = (timestamp) => {
    let d = new Date(timestamp);
    if (isNaN(d.getTime())) d = new Date(); // fallback for invalid date
    d.setHours(d.getHours() + 2); // +2 hour shift
    return d.toISOString();
  };

  /* Author: Lethabo Mazui
     Event: Sprint 1
     LatestUpdate: Load and update read/delivered messages
     Description: Marks all messages for this donation as read/delivered
  */
  useEffect(() => {
    if (!donationId || !channels) return;

    const updateChatStatus = async () => {
      const donationMessages = channels.filter(m => m.donationid === donationId);

      if (donationMessages.length > 0) {
        // Mark all messages as read
        await markChatRead(donationId);

        // Mark undelivered messages as delivered
        const hasUndelivered = donationMessages.some(m => !m.delivered && m.senderid !== currentUserId);
        if (hasUndelivered) await markDelivered(donationId);
      }
    };

    updateChatStatus();
  }, [donationId, channels, markChatRead, markDelivered, currentUserId]);

  /* Author: Lethabo Mazui
     Event: Sprint 1
     LatestUpdate: Scrolls messages container to bottom
     Description: Ensures latest message is visible
  */
  const scrollToBottom = () => {
    if (messagesEndRef.current) messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
  };
  useEffect(() => scrollToBottom(), [channels, donationId]);

  /* Author: Lethabo Mazui
     Event: Sprint 1
     LatestUpdate: Handles sending new messages
     Description: Sends message, clears input, and triggers addMessage from context
  */
  const handleSend = async () => {
    if (!inputValue.trim()) return;
    const timestamp = new Date().toISOString();
    addMessage(currentUserId, inputValue.trim(), donationId, timestamp);
    setInputValue("");
  };

  /* Author: Lethabo Mazui
     Event: Sprint 1
     LatestUpdate: Renders messages in chat
     Description: Displays messages with date bubbles, read/delivered ticks, and avatars
  */
  const renderMessages = () => {
    let lastDate = "";
    return channels
      .filter(m => m.donationid === donationId)
      .map(msg => {
        const shiftedISO = toLocalISO(msg.message_timestamp);
        const msgDate = new Date(shiftedISO);

        const showYear = msgDate.getFullYear() !== currentYear;
        const dateFormatter = new Intl.DateTimeFormat('en-US', {
          day: 'numeric',
          month: 'long',
          year: showYear ? 'numeric' : undefined
        });
        const formattedDate = dateFormatter.format(msgDate);

        const localTime = shiftedISO.slice(11, 16);
        const localDate = msgDate.toISOString().slice(0, 10);

        const showDate = lastDate !== localDate;
        lastDate = localDate;

        const isSent = msg.senderid === currentUserId;

        return (
          <React.Fragment key={msg.chatid + msg.message_timestamp}>
            {showDate && <div className="date-bubble">{formattedDate}</div>}
            <div className={`message-row ${isSent ? "sent" : "received"}`}>
              {!isSent && <div className="avatar">{msg.avatarEmoji}</div>}
              <div className={`message-bubble ${msg.readreceipts && !isSent ? "read-green" : ""}`}>
                {msg.chathistory}
                {isSent && (
                  <span className={`read-receipt ${msg.readreceipts ? "read" : msg.delivered ? "delivered" : ""}`}>
                    {msg.readreceipts ? "‚úì‚úì" : msg.delivered ? "‚úì‚úì" : "‚úì"}
                  </span>
                )}
              </div>
              <div className="message-time">{localTime}</div>
            </div>
          </React.Fragment>
        );
      });
  };

  /* Author: Lethabo Mazui
     Event: Sprint 1
     LatestUpdate: Main render
     Description: Renders chat UI including header, messages, typing indicator, and input
  */
  return (
    <div className="chat-page">
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate("/chats")}>‚Üê Back</button>
        <div className="chat-header-avatar">{avatarEmoji}</div>
        <div className="user-info">
          <div className="user-name">{otherParticipant}</div>
          <div className="user-subtitle" style={{ fontSize: "0.8rem", color: "grey" }}>
            ‚Ä¢ Donation #{donationId}
          </div>
        </div>
      </div>

      <div className="messages-wrapper">
        {renderMessages()}
        {typing && (
          <div className="message-row received">
            <div className="message-bubble typing"><span></span><span></span><span></span></div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <div className="message-input-container">
        <input
          type="text"
          placeholder="Type a message..."
          value={inputValue}
          onChange={e => setInputValue(e.target.value)}
          onKeyDown={e => e.key === "Enter" && handleSend()}
        />
        <button className="send-button" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}
