// src/users/HomeUser.js
import React, { useState } from "react";
import HomeDash from "./HomeComponents/HomeDash";
import HomeForm from "./HomeComponents/HomeForm";

export default function HomeUser({ currentUser }) {
  const [showForm, setShowForm] = useState(false);
  const [refreshFlag, setRefreshFlag] = useState(false); // to refresh dashboard after adding item

  const handleAddNew = () => setShowForm(true);
  const handleCloseForm = () => {
    setShowForm(false);
    setRefreshFlag(!refreshFlag); // refresh dashboard
  };

  return (
    <div className="dashboard-container">
      {showForm ? (
        <HomeForm currentUser={currentUser} onClose={handleCloseForm} />
      ) : (
        <HomeDash
          currentUser={currentUser}
          onAddNew={handleAddNew}
          refreshFlag={refreshFlag}
        />
      )}
    </div>
  );
}
